import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppContext } from '../Context/AppContext'; 

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

  const { state } = useContext(AppContext);

  const navigate = useNavigate();

  const handleCompanyClick = () => {
    // Navegar a la ruta de inicio cuando se hace clic en el nombre de la compañía
    navigate('/');
  };

  return (
    <nav className={`navbar ${state.theme === 'dark' ? 'navbar-dark' : 'navbar-light'}`}>
      {/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
      <div className="container">
      <Link className="navbar-brand" onClick={handleCompanyClick}>
        Nombre de la Compañía
      </Link>
        <div className="collapse navbar-collapse">
          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            <li className="nav-item">
              <Link className="nav-link" to="/">Home</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/contact">Contact</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to="/favs">Favorites</Link>
            </li>
          </ul> 
        </div>
      </div>
      
      {/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      <button>Change theme</button>
      <div>este es el navbar</div>
    </nav>
  )
}

export default Navbar
