// AppReducer.js
export const initialState = {
    theme: 'light',
    // Otros estados globales pueden ir aquí
  };
  
  export const appReducer = (state, action) => {
    switch (action.type) {
      case 'TOGGLE_THEME':
        return { ...state, theme: state.theme === 'light' ? 'dark' : 'light' };
      // Otros casos pueden ir aquí
      default:
        return state;
    }
  };
