//import React from 'react'
import Card from '../Components/Card'
import React, { useState, useEffect } from 'react'; 
import { Link } from 'react-router-dom';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {

  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Aquí se realiza la solicitud a la API para obtener la lista de 
    // dentistas y actualizar el estado 'dentists' con la respuesta:
    const fetchDentists = async () => {
      try {
        console.log('entro en el try');
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        
        setDentists(data);
        console.log('Dentists data:', data); // probando a ver si devuelve bien

      } catch (error) {
        console.error('Error fetching dentists:', error);
      }
    };

    fetchDentists();

  }, []);   //end useEffect
            // Ajusta el array de dependencias según tus necesidades.



  const handleAddToFavorites = (dentistId) => {
    // Aquí implementa la lógica para agregar al dentista con el ID dado a favoritos (LocalStorage).
  };




  return (
    <main className="" >
      <h1>Home</h1>
      <div className='card-grid' style={{ border: '1px solid red' }}>
      {dentists.map((dentist) => (
          <Card key={dentist.id}>
            <h3>{dentist.name}</h3>
            <p>{dentist.username}</p>
            <button onClick={() => handleAddToFavorites(dentist.id)}>ADD FAV</button>
            <Link to={`/dentist/${dentist.id}`}>Ver detalles</Link>
          </Card>
        ))}
      </div>
    </main>
  )
}

export default Home
