import React from "react";

import { Link } from 'react-router-dom';

const Card = ({ name, username, id }) => {
  console.log('name:', name, 'username:', username, 'id:', id); //depuracion

  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage:
    console.log(`Añadir a favoritos: ${id}`);
  }

  return (
    <div className="card" style={{ border: '1px solid red' }}>
      console.log('Props recibidas en Card - name:', name, 'username:', username, 'id:', id);
        {/* En cada card deberan mostrar en name - username y el id: */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}
 
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

        <h3>{name}</h3>
        <p>{username}</p>
        <Link to={`/dentist/${id}`}>Ver detalles</Link>

        <button onClick={addFav} className="favButton">Add fav</button>
    </div>
  );
};

export default Card;
