import React from "react";
import { Link } from 'react-router-dom';

const Card = ({ name, username, id }) => {
  const addFav = () => {
    // Aquí iría la lógica para agregar la Card en el localStorage:
    console.log(`Añadir a favoritos: ${id}`);
  };

  return (
    <div className="card h-100">
      <img
        src="../images/doctor.jpg"
        className="card-img-top"
        alt={`Image of ${name}`}
      />
      <div className="card-body d-flex flex-column">
        <h3 className="card-title">{name}</h3>
        <p className="card-text">{username}</p>
        <div className="mt-auto">
          <Link to={`/dentist/${id}`} className="btn btn-primary mx-auto">
            Ver detalles
          </Link>
          <button onClick={addFav} className="btn btn-outline-secondary mx-auto mt-2">
            Add to Favorites
          </button>
        </div>
      </div>
    </div>
  );
};

export default Card;
