import React from "react";
import { Link } from 'react-router-dom';


const Card = ({ name, username, id }) => {
  //console.log('name:', name, 'username:', username, 'id:', id); //depuracion

  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage:
    console.log(`Añadir a favoritos: ${id}`);
  }

  return (
    <div className="card">
      <img
        src="../images/doctor.jpg"
        className="card-img-top"
        alt={`Image of ${name}`}
      />
        <div className="card-body">
        {/* En cada card deberán mostrar el name, username y el id. */}
        <h3 className="card-title">{name}</h3>
        <p className="card-text">{username}</p>
        {/* No debes olvidar que la Card a su vez servirá como Link hacia la página de detalle. */}
        <Link to={`/dentist/${id}`} className="btn btn-primary">
          Ver detalles
        </Link>
        {/* Además, deberán integrar la lógica para guardar cada Card en el localStorage */}
        <button onClick={addFav} className="btn btn-outline-secondary">
          Add to Favorites
        </button>
      </div>
    </div>
  );
};

export default Card;
