//import React from 'react'
import Card from '../Components/Card'
import React, { useState, useEffect } from 'react'; 
//import { Link } from 'react-router-dom';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {
  const [dentists, setDentists] = useState([]);

  useEffect(() => {
    // Aquí se realiza la solicitud a la API para obtener la lista de 
    // dentistas y actualizar el estado 'dentists' con la respuesta:
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        //console.log('Dentists data:', data); // depuración
        setDentists(data);
      } catch (error) {
        console.error('Error fetching dentists:', error);
      }
    };

    fetchDentists();

  }, []); //end useEffect. Ajusta el array de dependencias según tus necesidades.


  const handleAddToFavorites = (dentistId) => {
    // Aquí implementa la lógica para agregar al dentista con el ID dado a favoritos (LocalStorage).
  };



  return (
    <main className="container">
      <h1 className="my-4">Our Dentists:</h1>
      <div className='row row-cols-1 row-cols-md-5 g-4'>
      {dentists.map((dentist) => (
        <div key={dentist.id} className='col'>
          <Card name={dentist.name} username={dentist.username} id={dentist.id} />
        </div>
        ))}
      </div>
    </main>
  );
}

export default Home
